import React, { Component } from "react";
//import storage from "../Firebase/index";
//

class ImageUpload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: null,
      url: "",
      progress: 0
    };
  }

  handleChange = e => {
    if (e.target.files[0]) {
      const image = e.target.files[0];
      this.setState(() => ({ image }));
    }
  };

  //   //handleuploadteststart
  //     handleUpload = evt => {
  //       let file = evt.target.files[0];
  //       let musicRef = f.storage().ref("images/" + file);

  //       musicRef.put(file).then(() => {
  //         const storageRefs = f.storage().ref("/images");
  //         storageRefs
  //           .child(file.name)
  //           .getMetadata()
  //           .then(metaData => {
  //             let url = metaData.downloadURLs[0];
  //             const messageRef = f.database().ref("url");
  //             messageRef.push({
  //               url: url,
  //               fileName: file.name
  //             });
  //           });
  //       });
  //     };

  //   componentDidMount() {
  //       const messageRef = f.database().ref('message');

  //       messageRef.on('child_added', snap => {
  //           this.setState({
  //               song: snap.val().song,
  //               songName: snap.val().songName
  //           });
  //       })
  //   }

  //handleuploadend

  handleUpload = async imageUrl => {
    const { image } = this.state;
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    // var photoObj = {
    //   url: imageUrl
    // };
    uploadTask.on(
      "state_changed",
      snapshot => {
        // progress function ...
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        this.setState({ progress });
      },
      error => {
        // Error function ...
        console.log(error);
      },
      () => {
        storage
          .ref("/images/")
          .child(image.name)
          .getDownloadURL()

          .then(url => {
            this.setState({ url });
            const messageRef = f.database().ref("downloadUrl");
            messageRef.push({
              url: url
            });
          });
      }
    );
  };

  render() {
    const messageRefs = f.database().ref("downloadUrl");
    return (
      <div className='center'>
        <br />
        <h2 className='green-text'>React Firebase Image Uploader</h2>
        <br />
        <br />
        <div className='row'>
          <progress
            value={this.state.progress}
            max='100'
            className='progress'
          />
        </div>
        <br />
        <br />
        <br />
        <div className='file-field input-field'>
          <div className='btn'>
            <span>File</span>
            <input type='file' onChange={this.handleChange} />
          </div>
          <div className='file-path-wrapper'>
            <input className='file-path validate' type='text' />
          </div>
        </div>
        <button
          onClick={this.handleUpload}
          className='waves-effect waves-light btn'
        >
          Upload
        </button>
        <br />
        <br />
        {
          <img
            src={this.state.url || "https://via.placeholder.com/400x300"}
            alt='Uploaded Images'
            height='300'
            width='400'
          />
        }
        <p>{messageRefs[1]}</p>
      </div>
    );
    // var imagesRef = storageRef.child("images");
    // console.log("hello");
  }
}

export default ImageUpload;
