class DashForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      style: "",
      budget: "",
      timeline: "",
      factory: "",
      projectDetails: ""
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    //this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    //alert("A name was submitted: " + this.state.value);
    //event.preventDefault();
    f.database()
      .ref()
      .set({
        title: this.refs.title.value,
        style: this.refs.style.value,
        budget: this.refs.budget.value,
        timeline: this.refs.timeline.value,
        factory: this.refs.factory.value,
        projectDetails: this.refs.projectDetails.value
      });
  }
  // handleSubmit2(event) {
  //   //alert("A name was submitted: " + this.state.value);
  //   event.preventDefault();
  //   f.database()
  //     .ref()
  //     .set({
  //       style: this.state.value
  //     });
  // }
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Project Name:
          <input
            ref='title'
            type='text'
            value={this.state.value}
            onChange={this.handleChange}
            placeholder={this.state.title}
          />
        </label>
        <label>
          Styles:
          <input
            ref='style'
            type='text'
            value={this.state.value}
            onChange={this.handleChange}
            placeholder={this.state.style}
          />
        </label>
        <label>
          Budget:
          <input
            ref='budget'
            type='text'
            value={this.state.value}
            onChange={this.handleChange}
            placeholder={this.state.budget}
          />
        </label>
        <label>
          Timeline:
          <input
            ref='timeline'
            type='text'
            value={this.state.value}
            onChange={this.handleChange}
            placeholder={this.state.timeline}
          />
        </label>
        <label>
          Factory Ready:
          <input
            ref='factory'
            type='text'
            value={this.state.value}
            onChange={this.handleChange}
            placeholder={this.state.factory}
          />
        </label>
        <label>
          Project Details
          <input
            ref='projectDetails'
            type='text'
            value={this.state.value}
            onChange={this.handleChange}
            placeholder={this.state.projectDetails}
          />
        </label>

        <input type='submit' value='Submit' />
      </form>
      //   <form onSubmit={this.handleSubmit2}>
